// starknet

import { connect } from "@argent/get-starknet"

export class ArgentXProvider {

    constructor() {
        this.icon = 'data:image/svg+xml;base64,'
        this.name = "ArgentX"
        this.version = '1.0.0'
        this.chains = []
        this.accounts = []
        this.features = {
            "standard:connect": {
                version: '1',
                connect: this.connect,
            },
            "standard:disconnect": {
                version: '1',
                disconnect: demo,
            },
            "standard:events": {
                version: '1',
                events: demo,
            },
            "standard:switchNetwork": {
                version: '1',
                switchNetwork: this.switchNetwork,
            },
            "sui:signAndExecuteTransactionBlock": {
                version: '1',
                signAndExecuteTransactionBlock: demo,
            },
        }
    }

    async connect() {
        const windowStarknet = await connect({
            include: ["argentX"],
        })
        const accounts = await windowStarknet?.enable({ starknetVersion: "v4" })

        return {
            "accounts": accounts.map(i => {
                return {
                    "address": i
                }
            })
        }

    }

    async switchNetwork(network_id) {
       throw new Error('not impl')
    }

    async getNetwork() {
        throw new Error('not impl')
    }
}


function demo() {
    console.log('demo')
}
